import { useContext, useEffect, useState } from "react"
import styled from "styled-components";
import AirTemperature from "../forcastComponents/AirTemperature";
import SwellPeriod from "../forcastComponents/SwellPeriod";
import WaveHeightDirection from "../forcastComponents/WaveHeight&Direction";
import WindDirectionSpeed from "../forcastComponents/WindDirection&Speed";

export default function Forecast({ selectedPointName, forcastList }) {

    const a = [{
        waveHeight: '6',
        airTemperature: 'airTemperature',
        currentDirection: 'currentDirection',
        windDirection: 'windDirection',
        swellPeriod: 'swellPeriod',
        windSpeed: 'windSpeed'
    }, {
        waveHeight: '5',
        airTemperature: 'airTemperature',
        currentDirection: 'currentDirection',
        windDirection: 'windDirection',
        swellPeriod: 'swellPeriod',
        windSpeed: 'windSpeed'
    }, {
        waveHeight: '4',
        airTemperature: 'airTemperature',
        currentDirection: 'currentDirection',
        windDirection: 'windDirection',
        swellPeriod: 'swellPeriod',
        windSpeed: 'windSpeed'
    }, {
        waveHeight: '3',
        airTemperature: 'airTemperature',
        currentDirection: 'currentDirection',
        windDirection: 'windDirection',
        swellPeriod: 'swellPeriod',
        windSpeed: 'windSpeed'
    }]

    const waveHeight = a.map(value => value.waveHeight);
    const currentDirection = a.map(value => value.currentDirection);
    const windDirection = a.map(value => value.windDirection);
    const windSpeed = a.map(value => value.windSpeed);

    return (
        <Wrapper>
            <WaveHeightDirection
            waveHeight={waveHeight}
            currentDirection={currentDirection}
            />
            <SwellPeriod
            a={a}
            />
            <WindDirectionSpeed
            windDirection={windDirection}
            windSpeed={windSpeed}
            />
            <AirTemperature
            a={a}
            />
        </Wrapper>
    )

}

const Wrapper = styled.div`
border: 2px solid orange;
width: 100%;
`
