import { useContext, useState } from "react"
import { ThreeDots } from "react-loader-spinner";
import { Link, useNavigate } from "react-router-dom";
import styled from "styled-components";
import UserContext from "../contexts/userContext";
import { postSignIn } from "../services/API.js";
import logoduck from "../assets/duck-head.png";

export default function SignIn() {
    const [form, setForm] = useState({
        name: "",
        password: ""
    });
    const [inputState, setInputState] = useState(false);
    const [loading, setLoading] = useState(true);
    const { setUserInfos } = useContext(UserContext);
    const navigate = useNavigate();

    function handleForm(e) {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        });
    }
    function signInRequest(e) {
        e.preventDefault();
        setInputState(true);
        setLoading(false);

        postSignIn(form).then((response) => {
            localStorage.setItem(
                "surfsup",
                JSON.stringify({
                    id: response.data.id,
                    name: response.data.user,
                    token: response.data.token
                })
            );
            setUserInfos({
                id: response.data.id,
                name: response.data.user
            })
            navigate('/');
            setInputState(false);
            setLoading(true);
            setForm({
                name: "",
                password: ""
            });
        }).catch((error) => {
            console.log(error);
            setForm({
                name: "",
                password: ""
            })
            setInputState(false);
            setLoading(true);
        });
    }
    return (
        <Wrapper>
            <Logo>
                <p>Surf's</p>
                <img
                    src={logoduck}
                />
                <p>Up</p>
            </Logo>
            <Form>
                <form onSubmit={signInRequest}>
                    <input
                        disabled={inputState}
                        required
                        type="text"
                        placeholder="user Name"
                        value={form.name}
                        onChange={handleForm}
                        name="name"
                    ></input>
                    <input
                        disabled={inputState}
                        required
                        type="password"
                        name="password"
                        placeholder="password"
                        value={form.password}
                        onChange={handleForm}
                    ></input>
                    {loading ? (
                        <button type="submit">Entrar</button>
                    ) : (
                        <button disabled={inputState}>
                            <ThreeDots color="#FFFFFF" height={20} width={50} />
                        </button>
                    )
                    }
                    <Link to="/signUp">
                        Primeira vez ? Se inscreva!
                    </Link>
                </form>
            </Form>
        </Wrapper>
    )
};

const Wrapper = styled.div`
width: 100vw;
height: 100vh;
border: 2px solid green;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
`

const Logo = styled.div`
display: flex;
border: 2px solid purple;
p{
    color: #68D2DF;
    font-family: 'Righteous';
    font-size: 50px;
}
img{
    width: 60px;
    height: 60px;
    margin: 0 5px 0 5px;
}
`
const Form = styled.div`
border: 2px solid red;
`
