import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import { deleteReport } from "../../services/API.js";
import logoduck from "../../assets/duck-head.png"

export default function Report({ comment, userName, stokedLevel, id, setReload, reload, date }) {
    const navigate = useNavigate();
    const slicedDate = date.slice(0, 10);
    const splitDate = slicedDate.split("-");

    function deleteReportRequest(id) {
        const user = JSON.parse(localStorage.getItem("surfsup"));

        if (!user) {
            alert("Faca login para comentar bro!");
            navigate('/signIn');
        } else {
            deleteReport(id).then((response) => {
                console.log(response.data);
                setReload(!reload);
            }).catch((error) => {
                console.log(error);
            });
        };
    }
    const stoked = stokedLevelDiv()

    function stokedLevelDiv() {
        if (stokedLevel === 'T치 flat bro') {
            return (
                <div>
                    <img src={logoduck} />
                </div>
            )
        } else if (stokedLevel === 'D치 pra brincar') {
            return (
                <div>
                    <img src={logoduck} />
                    <img src={logoduck} />
                </div>
            )
        } else {
            return (
                <div>
                    <img src={logoduck} />
                    <img src={logoduck} />
                    <img src={logoduck} />
                </div>
            )
        }
    }
    return (
        <Wrapper>
            <ReportDiv>
                <div>
                    <UserNameNDate>
                        <div>{userName}</div>
                        <p>{splitDate[2]}/{splitDate[1]}/{splitDate[0]}</p>
                    </UserNameNDate>
                    <Comment>
                        <UserComment>
                            <p>{comment}</p>
                        </UserComment>
                    </Comment>
                </div>
                <div>
                    <p>6m</p>
                    <p>SE</p>
                </div>
                <Stoked>
                    <>{stoked}</>
                </Stoked>
            </ReportDiv>
            <DeleteDiv>
                <ion-icon onClick={() => deleteReportRequest(id)} name="trash-outline"></ion-icon>
            </DeleteDiv>
        </Wrapper>
    )
}
const ReportDiv = styled.div`
width: 80%;
border-radius: 4%;
background-color:#9ccad3;
display: flex;
justify-content: space-between;
align-items: center;
padding: 4px;
`
const DeleteDiv = styled.div`
display: flex;
justify-content: center;
align-items: center;

ion-icon{
    font-size: 26px;
    color: #095E79;
}
`
const Stoked = styled.div`
border: 2px solid red;
display: flex;
flex-direction: column;
max-width: 100px;
img{
    height: 32px;
}
`
const Wrapper = styled.div`
display: flex;
flex-direction: row;
justify-content: space-between;
margin-top: 30px;
font-family: 'Lexend Deca';
font-style: normal;
font-weight: 500;
font-size: 19.976px;
line-height: 25px;
color: #095e79;
`
const UserNameNDate = styled.div`
font-style: italic;
display: flex;
margin: 4px 2px 0px 4px;
width: 70%;
justify-content: space-between;
border-bottom: 1px dashed #5B818A;
p{
    margin-left: 8px ;
}
`
const UserComment = styled.div`
width: 50%;
word-break:break-all;
p:nth-child(2){
    border: 2px solid white;
}
`
const Comment = styled.div`
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 8px;
`
