import { useNavigate } from "react-router-dom";
import styled from "styled-components"
import { deleteReport } from "../../services/API.js"

export default function Report({ comment, userName, stokedLevel, id, setReload, reload, date }) {
    const navigate = useNavigate();
    console.log(date)

    function deleteReportRequest(id) {
        const user = JSON.parse(localStorage.getItem("surfsup"));

        if (!user) {
            alert("Faca login para comentar bro!");
            navigate('/signIn');
        } else {
            deleteReport(id).then((response) => {
                console.log(response.data);
                setReload(!reload);
            }).catch((error) => {
                console.log(error);
            });
        };
    }

    return (
        <ReportDiv>
            <UserNameNDate>
                <div>{userName}</div>
                <p>{date}</p>
            </UserNameNDate>
            <Comment>
                <UserComment>
                    <p>{comment}</p>
                </UserComment>
                <div>
                    <p>6m</p>
                    <p>SE</p>
                </div>
                <ion-icon onClick={() => deleteReportRequest(id)} name="trash-outline"></ion-icon>
            </Comment>
        </ReportDiv>
    )
}

const ReportDiv = styled.div`
border: 2px solid red;
display: flex;
flex-direction: column;
justify-content: space-between;
margin-top: 10px;
`
const UserNameNDate = styled.div`
border: 2px solid purple;
display: flex;

p{
    margin-left: 6px ;
}
`
const UserComment = styled.div`
border: 2px solid pink;
width: 50%;
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;

p:nth-child(2){
    border: 2px solid white;
}
`
const Comment = styled.div`
    border: 2px solid green;
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;

    ion-icon{
    font-size: 26px;
    color: #095E79;
}
`
