import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import { deleteReport } from "../../services/API.js";
import logoduck from "../../assets/duck-head.png"
import commentImg from "../../assets/commentImg.png"

export default function Report({ comment, userName, stokedLevel, id, setReload, reload, date }) {
    const navigate = useNavigate();
    const slicedDate = date.slice(0, 10);
    const splitDate = slicedDate.split("-");

    function deleteReportRequest(id) {
        const user = JSON.parse(localStorage.getItem("surfsup"));

        if (!user) {
            alert("Faca login para comentar bro!");
            navigate('/signIn');
        } else {
            deleteReport(id).then((response) => {
                console.log(response.data);
                setReload(!reload);
            }).catch((error) => {
                console.log(error);
            });
        };
    }
    const stoked = stokedLevelDiv()

    function stokedLevelDiv() {
        if (stokedLevel === 'T치 flat bro') {
            return (
                <div>
                    <img src={logoduck} />
                </div>
            )
        } else if (stokedLevel === 'D치 pra brincar') {
            return (
                <div>
                    <img src={logoduck} />
                    <img src={logoduck} />
                </div>
            )
        } else {
            return (
                <div>
                    <img src={logoduck} />
                    <img src={logoduck} />
                    <img src={logoduck} />
                </div>
            )
        }
    }
    return (
            <CommentBox>
                <div class="arrow">
                    <div class="outer"></div>
                    <div class="inner"></div>
                </div>
                <div class="message-body">
                    <div>
                    <UserNameNDate>
                       <div>{userName}</div>
                       <p>{splitDate[2]}/{splitDate[1]}/{splitDate[0]}</p>
                   </UserNameNDate>
                    </div>
                </div>
            </CommentBox>
    )
}

const CommentBox = styled.div`
  clear: both;
  position: relative;

  .message-body {
  float: left;
  width: 80%;
  height: auto;
  border: 1px solid #CCC;
  background-color: #ffffff;
  height: 200px;
  border: 1px solid #000000;
  padding: 6px 8px;
  -webkit-border-radius: 5px;
  -moz-border-radius: 5px;
  -o-border-radius: 5px;
  border-radius: 5px;
  margin-bottom: 14px;

  p {
  margin: 0;
}
}

  .arrow {
  width: 12px;
  height: 20px;
  overflow: hidden;
  position: relative;
  float: left;
  top: 6px;
  right: -1px;

  .outer {
  width: 0;
  height: 0;
  border-right: 20px solid #000000;
  border-top: 10px solid transparent;
  border-bottom: 10px solid transparent;
  position: absolute;
  top: 0;
  left: 0;
}

.inner {
  width: 0;
  height: 0;
  border-right: 20px solid #ffffff;
  border-top: 10px solid transparent;
  border-bottom: 10px solid transparent;
  position: absolute;
  top: 0;
  left: 2px;
}
}
`
const UserNameNDate = styled.div`
font-style: italic;
width: 10rem;
display: flex;
justify-content: space-between;
border-bottom: 1px dashed #5B818A;
p{
   margin-left: 8px ;
}
`
